exports.step = function(input)
{

var lastReported= input.LastReportedMileage;

var sellerReported=input.SellerReported;

var currentExpected = input.CurrentExpected;

var Obj = new Object();

Obj['lastReported']=format(lastReported);

Obj['sellerReported']=format(sellerReported);

Obj['currentExpected']=format(currentExpected);

 function format(x)
{

if (x>0 && x<10)
{
x=x.toString();
x= '0' + x ;
}

else
{
x=x.toString();
x=x.replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

return x; 

}


return Obj;
}
